package while_for_dowhile;

public class dowhile_learning {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;
		int i=100;
		do
		{
			sum+=i;
			i++;
		}while(i<200);
		System.out.println("The sum is " +sum);
	}

}


//The sum is 14950 (same as while.java)
